select
t.id,t.test_money as testMoney,t.coach_money as separateMoney,t.test_time as testTime,t.plate_number as
plateNumber,o.order_no as orderNo
from tab_separate_account t
left join tab_order o on t.order_id=o.order_id


        and t.coach_id = #{coachId}


        and t.coach_name = #{coachName}


        and t.create_time >=DATE_FORMAT( #{starDate,jdbcType=VARCHAR},'%Y-%m-%d %H:%m:%s')


        and t.create_time <= DATE_FORMAT( #{endDate,jdbcType=VARCHAR},'%Y-%m-%d %H:%m:%s')




        order by ${sort} ${order}


        order by t.id desc



    limit #{offset}, #{limit}

select t.id,t.test_money as testMoney,t.agent_1_money as separateMoney,t.test_time as testTime,t.plate_number as plateNumber,o.order_no as orderNo from tab_separate_account t left join tab_order o on t.order_id=o.order_id
